create database blogDiablo;

use blogDiablo;

create table imgPerfil (
id int primary key auto_increment,
url varchar(16000)
)auto_increment = 10000;

create table cadastro (
  idCadastro int,
  fkUsuario int,
  foreign key fkUsuario references usuario(id),
  constraint pkAssociativa primary Key (idCadastro, fkUsuario)
)

create table usuario (
id int primary key auto_increment,
nome varchar(45),
sobrenome varchar(100),
nomeUsuario varchar(45) unique,
email varchar(100) unique,
senha varchar(100),
fkImg int,
foreign key (fkImg) references imgPerfil (id)
);

create table comentario (
id int primary key auto_increment,
titulo varchar(45),
descricao varchar(1024),
fkUsuario int,
foreign key (fkUsuario) references usuario (id)
)auto_increment = 100;

CREATE TABLE enquete (
  id INT PRIMARY KEY AUTO_INCREMENT,
  atos CHAR(5) NOT NULL,
  contador_votos INT DEFAULT 0,
  fkUsuario int,
  foreign key (fkUsuario) references usuario (id)
);

CREATE TABLE classe (
id int primary key,
cacadorDeDemonio varchar(45),
necromante varchar(45),
barbaro varchar(45),
mago varchar(45),
feiticeiro varchar(45),
cruzado varchar(45)
);
CREATE TABLE usuarioClasse (
nome varchar(45),
nivel varchar(45),
fkUsuario int,
foreign key (fkUsuario) references usuario (id),
fkClasse int,
foreign key (fkClasse) references classe (id)
);


